import './App.css';
import axios from 'axios';

function App() {
  const uploadPBT = (event) => {
    const input = event.target.files[0];

    let config = {
      method: 'post',
      maxBodyLength: Infinity,
      url: 'http://localhost:3000/plan_names',
      data : input
    };

    axios.request(config)
      .then((response) => {
        console.log(JSON.stringify(response.data));
      })
      .catch((error) => {
        console.log(error);
      });
  }

  return (
    <div className="App">
      <div className="container-fluid">
        <div className="row">
          <div className="col-4">
            <div className="row">
              <div className="mb-3">
                <label htmlFor="formFile" className="form-label">Upload PBT</label>
                <input className="form-control" type="file" id="formFile" onChange={(e) => uploadPBT(e)} accept="application/vnd.ms-excel.sheet.macroenabled.12" />
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
