import React, { useState } from "react";
import axios from 'axios';

export default function Uploader() {

  const uploadPBT = (event) => {
    const input = event.target.files[0];
    let data = new FormData()
    console.log(input)

    data.append('file', input)

    let config = {
      method: 'post',
      maxBodyLength: Infinity,
      url: 'http://localhost:3001/plan_names',
      data : data
    };

    axios.request(config)
      .then((response) => {
        console.log(JSON.stringify(response.data));
      })
      .catch((error) => {
        console.log(error);
      });
  }

  return (
    <div className="row">
      <div className="col-4">
        <div className="row">
          <div className="mb-3">
            <label htmlFor="formFile" className="form-label">Upload PBT</label>
            <input className="form-control" type="file" id="formFile" onChange={(e) => uploadPBT(e)} accept="application/vnd.ms-excel.sheet.macroenabled.12" />
          </div>
        </div>
      </div>
    </div>
  )
}
