import React, { useState } from "react";
import axios from 'axios';

export default function Uploader() {
  const [pbtOptions, setPBTOptions] = useState([])
  const [pbtFile, setPBTFile] = useState()
  const [pbtTableData, setPbtTableData] = useState([])
  const uploadPBT = (event) => {
    const input = event.target.files[0];
    let data = new FormData()

    data.append('file', input)

    setPBTFile(input)

    let config = {
      method: 'post',
      maxBodyLength: Infinity,
      url: 'http://localhost:3001/plan_names',
      data : data
    };

    axios.request(config)
      .then((response) => {
        console.log(JSON.stringify(response.data));
        setPBTOptions(response.data)
      })
      .catch((error) => {
        console.log(error);
      });
  }

  const selectPlan = (e) => {
    const pbtOption = pbtOptions.find(pbt => pbt.key === e.target.value)

    console.log(pbtOption)
    console.log(pbtFile)

    let data = new FormData()

    data.append('plan_name', pbtOption.plan_name);
    data.append('sheet_name', pbtOption.sheet_name);
    data.append('file', pbtFile);

    let config = {
      method: 'post',
      maxBodyLength: Infinity,
      url: 'http://localhost:3001/plan_details',
      data : data
    };

    axios.request(config)
    .then((response) => {
      console.log(JSON.stringify(response.data));
      setPbtTableData([response.data])
    })
    .catch((error) => {
      console.log(error);
    });

  }

  return (
    <div className="row">
      <div className="col-4">
        <div className="row">
          <div className="mb-3">
            <label htmlFor="formFile" className="form-label">Upload PBT</label>
            <input className="form-control" type="file" id="formFile" onChange={(e) => uploadPBT(e)} accept="application/vnd.ms-excel.sheet.macroenabled.12" />

            { pbtOptions.length > 0 && 
              <select className="form-select mt-3" aria-label="Default select example" onChange={(e) => selectPlan(e)}>
                <option defaultValue>Select Plan</option>
                { pbtOptions.map(option => <option value={option?.key} key={option?.key}>{option?.plan_name}</option>) }
              </select>
            }

            { pbtTableData &&

              <p>{pbtTableData}</p>
            }


          </div>
        </div>
      </div>
    </div>
  )
}
